sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var rec = new GlideRecord('x_1117940_games_user_games');

	var ratings = '';
	var total = 0;
	var count = 0;

	rec.addQuery('u_game', current.u_game);
	rec.query();

	gs.info('records before while = ' + rec);
	while (rec.next()) {
		if (rec.getValue('number') != current.number){
			ratings = rec.getValue('rating');}

		else { ratings = current.rating.toString(); }
		gs.info('record = ' + ratings);
		count += 1;
		total += ratingsCheck(ratings);
	}

	var average = total/count;
	gs.info(' count = ' + count + ' total = ' + total + ' average = ' + average);

	var recGames = new GlideRecord('x_1117940_games_master_catalogue');
	recGames.query();

	while (recGames.next()) {
		var num = recGames.getValue('sys_id').toString();
		gs.info('recGames number: ' + num + ' current.number: ' + current.u_game);

		if (num == current.u_game){
		recGames.setValue('average_ratings', average);
		recGames.update();}
	}

	})(current, previous);
