sys_script_include.script:
var TotalBWR = function(status, userGame) {
	var num;
	switch(status){
		case 'brought':
			num = userGame.getValue('number_of_buys');
			num = parseInt(num);
			num += 1;
			userGame.setValue('number_of_buys', num);
			userGame.update();
			break;
		case 'ref':
			num = userGame.getValue('number_of_refunds');
			num = parseInt(num);
			num += 1;
			userGame.setValue('number_of_refunds', num);
			userGame.update();
			break;
		case 'wishlisted':
			num = userGame.getValue('number_of_wishlist');
			num = parseInt(num);
			num += 1;
			userGame.setValue('number_of_wishlist', num);
			userGame.update();
			break;
	}
};
